import {useState} from 'react'
// you might come across the use of useState() like this in the wild
// import React from 'react'
// React.useState()
// this is the library we installed, we are importing the favorite.svg as a react component
// RENamed to heart
import {ReactComponent as Heart} from '@material-design-icons/svg/filled/favorite.svg'

export default function UserRating() {
  const [count, setCount] = useState(0)
  const removeButton = true

  const handlePlusClick = () => {
    if (count < 5) {
      setCount(count + 1)
    }
    if (count === 5){
      {removeButton || <button/>}
    }
    return
  }

  const handleMinusClick = () => {
    if (count > 0) {
      setCount(count - 1)
    }
    if (count === 0){
      {removeButton || <button/>}
    }
    return
  }

  // const removal = () => {
  //   if (count === 5) {
  //     {removeButton || <button onClick={handlePlusClick}/>}
  //   }
  //   if (count === 0){
  //     {removeButton || <button onClick={handleMinusClick}/>}
  //   }
  // }


  return (
    <div>
      <button onClick={handleMinusClick}>[-]</button>
      <span>
        {/* {count} */}
        {[...Array(count)].map((heart, i) => {
          return (
            <span key="i">
              <Heart />
            </span>
          )
        })}
      </span>
      <button onClick={handlePlusClick}>[+]</button>
    </div>
  )
}
