import ReactDOM from 'react-dom'

export default function Modal(props) {
  const {onClose, children, actionBar} = props
  return ReactDOM.createPortal(
    <>
    {/* Overlay */}
    <div 
    onClick = {onClose} 
    className='fixed inset-0 bg-gray-300 opacity-50'
    ></div>
     {/* Modal Content */}
      <div className='fixed inset-40 p-10 bg-white'>
      <div className="flex flex-col jusitfy-between h-full">{children}</div>
      {/* Action Bar */}
      <div className="flex flex-row justify-end">{actionBar}</div>
    </div>
    </>, 
    document.getElementById('portal')
  )
}
