import {useState} from 'react'

import Button from '../components/Button'
import Modal from '../components/Modal'

export default function ModalPage() {
  const [isModalOpen, setIsModalOpen] = useState(true)
  const handleClick = () => {
    setIsModalOpen(true)
  }

  const handleClose = () => {
    setIsModalOpen(false)
  }

  const actionBar = (
    <div>
      <Button danger onClick={() =>console.log('clicked the danger button')}>
      Delete</Button>
      <Button success onClick={handleClose}></Button>
    </div>
  )

  const modal = (<Modal onClose={handleClose}>
    This is a very important modal for you to accept or decline
    </Modal>
  )

  return (
    <div className="relative w-96 overflow-hidden">
      <Button primary outline className="m-4" onClick={handleClick}>
        Open Modal
      </Button>
      <Button danger outline className="m-4" onClick={handleClose}>
        Close Modal
      </Button>
      {isModalOpen && modal}
    </div>
  )
}
