export default function Modal(props) {
  const {onClose, children} = props
  return (
    <>
    {/* Overlay */}
    <div 
    onClick = {onClose} 
    className='fixed inset-0 bg-gray-300 opacity-50'>
    </div>
    {/* Modal Content */}
    <div className='absolute inset-40 p-10 bg-white'>{children}</div>
    </>
  )
}
