    onClick = {onClose} 
    className='fixed inset-0 bg-gray-300 opacity-50'>
    </div>
    <div>{/* Modal Content */}
    <div className='absolute inset-40 p-10 bg-white'>{children}</div>
    {/* Action Bar */}
    <div>{actionBar}</div>
    </div>
    </>, 
    document.getElementById('portal')
  )
}
