import {useState} from 'react'
const Dropdown = (props) => {
  const [isOpen, setIsOpen] = useState(false)
  const {options, onChange, value} = props

  const handleClick = () => {
    //technically, we should be using functional updates for state
    //setIsOpen((currentIsOpen) => !currentIsOpen)
    // but this also works and you'll see this in examples a lot as well
    setIsOpen(!isOpen)
  }

  const handleOptionClick = (option)=> {
    setIsOpen(false)
    onChange(option)
  }

  const renderedOptions = options.map((option) => {
    return( <div onClick={() => handleOptionClick(option)} key={option.value}>{option.label}</div>
  )})

  let content = 'Select...'
  if (value) {
    content = value.label
  }

  return (
    <div>
      <div onClick= {handleClick}> {content}</div>
      {isOpen && <div>{renderedOptions}</div>}
    </div>
  )
}

export default Dropdown
