import { useState, useEffect } from 'react'
import axios from 'axios'
import TodoCreate from './components/TodoCreate'
import TodoList from './components/TodoList'
import PackingList from './components/PackingList' // Import the new PackingList component

const App = () => {
  const [todos, setTodos] = useState([])

  // Function to get the todo list from the server
  const fetchTodos = async () => {
    const response = await axios.get('http://localhost:3001/todos')
    setTodos(response.data)
  }

  // Run this function once when the app loads
  useEffect(() => {
    fetchTodos()
  }, [])

  // Function to create a new todo item
  const createTodo = async (title) => {
    const response = await axios.post('http://localhost:3001/todos', { title })
    const updatedTodos = [...todos, response.data]
    setTodos(updatedTodos)
  }

  // Function to edit a todo item
  const editTodoById = async (id, newTitle) => {
    const response = await axios.put(`http://localhost:3001/todos/${id}`, {
      title: newTitle,
    })
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, ...response.data }
      }
      return todo
    })
    setTodos(updatedTodos)
  }

  // Function to delete a todo item
  const deleteTodoById = async (id) => {
    await axios.delete(`http://localhost:3001/todos/${id}`)
    const updatedTodos = todos.filter((todo) => todo.id !== id)
    setTodos(updatedTodos)
  }

  // The main app layout
  return (
    <div>
      <TodoCreate onCreate={createTodo} />
      <TodoList todos={todos} onDelete={deleteTodoById} onEdit={editTodoById} />
      <PackingList /> {/* Add your new component here */}
    </div>
  )
}

export default App
